import React, { useState } from "react";
import './Form.css';

function Form(props) {
  const [name, setName] = useState('');


  function handleSubmit(e) {
    e.preventDefault();
    if (!name.trim()) {
      return;
    }
    props.addTask(name);
    setName("");
  }


  function handleChange(e) {
    setName(e.target.value);
  }

  return (
    <>
    <div className="container">
    <div className="form">

    <form onSubmit={handleSubmit}>
      <label className="label-wrapper">
          What needs to be done?
        </label>

      <br/>
      <input
        type="text"
        id="new-todo-input"
        name="text"
        autoComplete="off"
        value={name}
        onChange={handleChange}
      />
      <br/>
      <div className="btn">
      <button style={{marginLeft:"400px",backgroundColor:"black",
      color:"white",width:"700px",height:"50px", padding: "15px 30px",border:"none",marginTop:"10px"}}type="submit" >
      Add
    </button>
      </div>

    </form>
    </div>
    </div>
    </>
  );
}

export default Form;
